    // Respectful neurotype detection based on user preferences
    const saved = localStorage.getItem('neurotype-preference');
    if (saved) return saved;

    // Offer neurotype selection with explanation of benefits
    return this.presentNerotypeSelection();
  }

  initializeTheme() {
    return {
      mode: 'dark',
      neurotype: this.neurotype,
      accessibility: this.calculateAccessibilitySettings(),
      animations: this.determineAnimationPreferences(),
      contrast: this.optimizeContrastRatios(),
      colors: this.generateNeuroTypeSpecificPalette()
    };
  }

  applyTheme() {
    document.documentElement.setAttribute('data-theme', 'dark');
    document.documentElement.setAttribute('data-neurotype', this.neurotype);

    this.injectCustomProperties();
    this.initializeMicroInteractions();
    this.setupAccessibilityFeatures();
    this.enableSmartAnimations();
  }
}
