class MobileAnalytics {
  constructor() {
    this.deviceInfo = this.getDeviceInfo();
    this.trackMobileInteractions();
  }

  getDeviceInfo() {
    return {
      screenSize: `${screen.width}x${screen.height}`,
      pixelRatio: window.devicePixelRatio,
      orientation: screen.orientation?.type || 'unknown',
      touchSupport: 'ontouchstart' in window,
      connectionType: navigator.connection?.effectiveType || 'unknown',
      platform: navigator.platform,
      userAgent: navigator.userAgent
    };
  }

  trackMobileInteractions() {
    // Track touch vs mouse interactions
    let interactionType = 'unknown';

    document.addEventListener('touchstart', () => {
      interactionType = 'touch';
    }, { once: true });

    document.addEventListener('mouseover', () => {
      if (interactionType === 'unknown') {
        interactionType = 'mouse';
      }
    }, { once: true });

    // Track scroll behavior
    let scrollDepth = 0;
    window.addEventListener('scroll', () => {
      const depth = Math.round(
        (window.scrollY / (document.body.scrollHeight - window.innerHeight)) * 100
      );
      scrollDepth = Math.max(scrollDepth, depth);
    });

    // Track orientation changes
    screen.orientation?.addEventListener('change', () => {
      this.logEvent('orientation-change', {
        new_orientation: screen.orientation.type,
        angle: screen.orientation.angle
      });
    });
  }
}
