    return {
      touch: 'ontouchstart' in window,
      hover: matchMedia('(hover: hover)').matches,
      highDPI: window.devicePixelRatio > 1,
      darkMode: matchMedia('(prefers-color-scheme: dark)').matches,
      reducedMotion: matchMedia('(prefers-reduced-motion: reduce)').matches,
      networkSpeed: navigator.connection?.effectiveType || 'unknown',
      battery: 'getBattery' in navigator
    };
  }

  applyEnhancements() {
    if (this.capabilities.touch && \!this.capabilities.hover) {
      document.body.classList.add('touch-device');
      this.optimizeForTouch();
    }

    if (this.capabilities.highDPI) {
      document.body.classList.add('high-dpi');
      this.loadHighResAssets();
    }

    if (this.capabilities.networkSpeed === 'slow-2g' || 
        this.capabilities.networkSpeed === '2g') {
      document.body.classList.add('slow-connection');
      this.optimizeForSlowConnection();
    }
  }

  optimizeForTouch() {
    // Increase touch targets
    document.documentElement.style.setProperty('--min-touch-size', '48px');

    // Disable hover effects
    document.body.classList.add('no-hover');

    // Enable touch-friendly scrolling
