        statusRegion.id = 'status-region';
        statusRegion.setAttribute('aria-live', 'polite');
        statusRegion.setAttribute('aria-label', 'Status updates');
        statusRegion.className = 'visually-hidden';
        document.body.appendChild(statusRegion);

