// Advanced image optimization with WebP support
class ImageOptimizer {
  constructor() {
    this.setupResponsiveImages();
    this.implementLazyLoading();
  }

  setupResponsiveImages() {
    const images = document.querySelectorAll('img[data-responsive]');
    images.forEach(img => {
      const picture = document.createElement('picture');

      // WebP source for modern browsers
      const webpSource = document.createElement('source');
      webpSource.srcset = this.generateWebPSrcSet(img.dataset.src);
      webpSource.type = 'image/webp';

      // Fallback JPEG source
      const jpegSource = document.createElement('source');
      jpegSource.srcset = this.generateJPEGSrcSet(img.dataset.src);
      jpegSource.type = 'image/jpeg';

      picture.appendChild(webpSource);
      picture.appendChild(jpegSource);
      picture.appendChild(img);

      img.parentNode.insertBefore(picture, img);
    });
  }

  generateWebPSrcSet(baseSrc) {
    return `
      ${baseSrc}-400.webp 400w,
      ${baseSrc}-800.webp 800w,
      ${baseSrc}-1200.webp 1200w
    `;
  }
}
```

