  }

  checkAlerts(metrics) {
    const alerts = [];

    Object.keys(this.thresholds).forEach(metric => {
      const value = this.extractMetricValue(metrics, metric);
      const threshold = this.thresholds[metric];

      if (value < threshold.critical) {
        alerts.push({
          level: 'critical',
          metric: metric,
          value: value,
          threshold: threshold.critical,
          message: `${metric} critically low at ${value}`,
          action_required: true
        });
      } else if (value < threshold.warning) {
        alerts.push({
          level: 'warning',
          metric: metric,
          value: value,
          threshold: threshold.warning,
          message: `${metric} below target at ${value}`,
          action_required: false
        });
      }
    });

    if (alerts.length > 0) {
      this.sendAlerts(alerts);
    }

    return alerts;
  }

  sendAlerts(alerts) {
    // Console logging for development
    alerts.forEach(alert => {
      console.warn(`Quality Alert: ${alert.message}`);
    });

    // Future: Email notifications, Slack integration, etc.
