  analyzeTrends(currentMetrics) {
    const trends = {
      seo: this.calculateTrend('seo', currentMetrics.seo.score),
      performance: this.calculateTrend('performance', currentMetrics.performance.score),
      accessibility: this.calculateTrend('accessibility', currentMetrics.accessibility.score),
