      trends,
      insights: this.generateInsights(trends),
      recommendations: this.generateRecommendations(trends, currentMetrics)
    };
  }

  calculateTrend(metric, currentValue) {
    const recentData = this.getRecentData(metric, 30); // Last 30 days
    if (recentData.length < 2) return { trend: 'insufficient_data' };

    const slope = this.calculateLinearRegression(recentData);
    const percentChange = ((currentValue - recentData[0].value) / recentData[0].value) * 100;

    return {
      direction: slope > 0 ? 'improving' : slope < 0 ? 'declining' : 'stable',
      slope: slope,
      percent_change: percentChange,
      confidence: this.calculateTrendConfidence(recentData)
    };
  }

  generateInsights(trends) {
    const insights = [];

    if (trends.seo.direction === 'improving') {
      insights.push({
        type: 'positive',
        category: 'SEO',
        message: `SEO score improving by ${trends.seo.percent_change.toFixed(1)}% over the last 30 days`,
        impact: 'Professional visibility and search engine discoverability increasing'
      });
    }

    if (trends.performance.direction === 'declining') {
      insights.push({
        type: 'warning',
        category: 'Performance',
        message: `Performance score declining by ${Math.abs(trends.performance.percent_change).toFixed(1)}%`,
        impact: 'User experience and Core Web Vitals may need attention'
      });
    }

    return insights;
  }
}
