### Mobile SDK
```swift
// iOS SDK Example
import OrchestrixSDK

class AgentManager {
    let client = OrchestrixClient(apiKey: "...")

    func executeAgent() async throws {
        let result = try await client.agents.execute(
            agentId: "SUPPORT_MAESTRO",
            input: ["customerId": "12345"],
            timeout: 5000
        )

        await MainActor.run {
            self.updateUI(with: result)
        }
    }

    func monitorWorkflow() {
        client.workflows.monitor(workflowId: "...") { event in
            switch event {
            case .started: showNotification("Workflow started")
            case .completed(let result): handleCompletion(result)
            case .failed(let error): handleError(error)
            }
        }
    }
}
```

